import React, { Component } from 'react'
import proyectoStyle from './ContactanosStyle/Contactanos.module.css';
import emailjs from '@emailjs/browser';

export default class Contactanos extends Component {

    render() {

        document.addEventListener("DOMContentLoaded", function () {
            // your code here
            const btn = document.getElementById("button");

            if (btn) {
                document.getElementById("form").addEventListener("submit", function (event) {
                    event.preventDefault();
                    btn.value = "Sending...";
                    emailjs.sendForm("service_d30033t", "template_yfbeqdq", this).then(
                        () => {
                            btn.value = "Send Email";
                            alert("Sent!");
                            document.querySelector("#from_name").value = "";
                            document.querySelector("#to_name").value = "Atrevete Team";
                            document.querySelector("#message").value = "";
                            document.querySelector("#reply_to").value = "Atrevete";
                            document.querySelector("#phone_number").value = "";
                            document.querySelector("#mail").value = "";
                        },
                        (err) => {
                            btn.value = "Send Email";
                            alert(JSON.stringify(err));
                        }
                    );
                });
            }


        });
        return (
            <div className={proyectoStyle.generalProyecto}>
                <h3 className={proyectoStyle.titulo}>Contactanos</h3>
                <div className={proyectoStyle.cuentanos}>
                    <form id="form">
                        <div class="field">
                            <label for="from_name">Nombre :</label>
                            <input type="text" name="from_name" id="from_name" required></input>
                        </div>
                        <div class="field">
                            <label for="message">Mensaje :</label>
                            <input type="text" name="message" id="message" required></input>
                        </div>

                        <div class="field">
                            <label for="phone_number">Numero de telefono :</label>
                            <input type="text" name="phone_number" id="phone_number" required> </input>
                        </div>
                        <div class="field">
                            <label for="mail">E-mail :</label>
                            <input type="text" name="mail" id="mail" required> </input>
                        </div>

                        <input type="submit" id="button" value="Send Email"></input>
                    </form>

                    <script type="text/javascript" src="https://cdn.jsdelivr.net/npm/emailjs-com@2/dist/email.min.js"></script>

                    <script type="text/javascript">
                        emailjs.init('user_1J9lK3QggxonWQXgni1Wr')
                    </script>
                </div>
            </div>
        )
    }
}
